import React, { useEffect } from 'react'

const MapComponenet = ({ threatLocation, planeLocation }) => {
  const mapRef = useRef(null);
  
  useEffect(() => {
    mapRef.current = new google.maps.Map(document.getElementById('map-container'), {
        center: {lat: 0, lng: 0},
        zoom: 2
    });

    if(threatLocation) {
        new google.maps.Marker({
            position: { lat: threatLocation.latitude, lng: threatLocation.longitude },
            map: mapRef.current,
            title: 'Threat Location'
        });
    }

    if(planeLocation) {
        new google.maps.Marker({
            position: { lat:planeLocation.latitude, lng: planeLocation.longitude },
            map: mapRef.current,
            title: 'Friendly Plane Location'
        });
    }
  }, [threatLocation, planeLocation]);

  return (
    <div id="map-container" style={{ width: '100%', height: '400px' }}></div>
  );
}

export default MapComponenet
